package uz.example.dagger_2_android3.core.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport uz.example.dagger_2_android3.core.model.HomeGroupItemimport uz.example.dagger_2_android3.databinding.HomeGroupItemBindingclass HomeAdapter : RecyclerView.Adapter<HomeAdapter.ViewHolder>() {    private val data = ArrayList<HomeGroupItem>()    fun setData(homeGroupItem: HomeGroupItem) {        data.add(homeGroupItem)        data.sortBy { it.type }        notifyItemInserted(data.indexOf(homeGroupItem))    }    inner class ViewHolder(val binding: HomeGroupItemBinding) : RecyclerView.ViewHolder(binding.root){        private val adapter=HomeMovieAdapter()        fun bindData(d: HomeGroupItem) {            binding.homeTitle.text = d.title            binding.movieList.adapter=adapter            binding.movieList.layoutManager = LinearLayoutManager(binding.root.context, LinearLayoutManager.HORIZONTAL, false)            adapter.data=d.data        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        return ViewHolder(HomeGroupItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {    holder.bindData(data[position])    }    override fun getItemCount(): Int {        return data.size    }}