package uz.example.dagger_2_android3.core.adapter.helperimport android.graphics.Rectimport android.view.Viewimport androidx.recyclerview.widget.RecyclerViewimport androidx.recyclerview.widget.RecyclerView.ItemDecorationclass HorizontalDecoration(private val margin:Int) : ItemDecoration() {    override fun getItemOffsets(outRect: Rect, view: View, parent: RecyclerView, state: RecyclerView.State) {        super.getItemOffsets(outRect, view, parent, state)        val total=parent.adapter!!.itemCount        val current=parent.getChildAdapterPosition(view)        outRect.left=margin/2        outRect.right=margin/2        if (current == 0) {            outRect.left=margin        }        if (current == total - 1) {            outRect.right=margin        }    }}