package uz.example.dagger_2_android3.core.baseimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.beepul.lite.chat.utils.SingleLiveEventimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.CoroutineStartimport kotlinx.coroutines.launchimport kotlin.coroutines.CoroutineContextimport kotlin.coroutines.EmptyCoroutineContextopen class BaseViewModel : ViewModel() {    fun launch(        context: CoroutineContext = EmptyCoroutineContext,        start: CoroutineStart = CoroutineStart.DEFAULT,        block: suspend CoroutineScope.() -> Unit) = viewModelScope.launch(context, start, block)    val errorLiveData=SingleLiveEvent<String?>()}