package uz.example.dagger_2_android3.core.di.module.networkimport dagger.Moduleimport dagger.Providesimport retrofit2.Retrofitimport uz.example.dagger_2_android3.core.di.annotation.MovieDBAnnotationimport uz.example.dagger_2_android3.core.di.annotation.NewsApiAnnotationimport uz.example.dagger_2_android3.core.network.NewsApiimport uz.example.dagger_2_android3.core.network.MovieDbApiimport javax.inject.Namedimport javax.inject.Singleton@Moduleobject ServiceModule {    @Provides    @Singleton    fun provideNewsApi(        @NewsApiAnnotation        retrofit: Retrofit    ):NewsApi {        return retrofit.create(NewsApi::class.java)    }    @Provides    @Singleton    @Named("salom")    fun createDefaultString():String {        return "Salom"    }    @Provides    @Singleton    @Named("xayr")    fun createXayrString():String {        return "Xayr"    }    @Provides    @Singleton    fun provideWeatherApi(        @MovieDBAnnotation        retrofit: Retrofit    ): MovieDbApi {        return retrofit.create(MovieDbApi::class.java)    }}