package uz.example.dagger_2_android3.core.di.module.networkimport dagger.Moduleimport dagger.Providesimport retrofit2.Retrofitimport uz.example.dagger_2_android3.core.network.MovieDbApiimport javax.inject.Singleton@Moduleobject ServiceModule {    @Provides    @Singleton    fun provideMovieApi(        retrofit: Retrofit    ): MovieDbApi {        return retrofit.create(MovieDbApi::class.java)    }}