package uz.example.dagger_2_android3.core.di.module.repoimport dagger.Moduleimport dagger.Providesimport uz.example.dagger_2_android3.core.network.NewsApiimport uz.example.dagger_2_android3.core.repo.MainRepoimport javax.inject.Singleton@Moduleclass RepositoriesModule {    @Provides    @Singleton    fun provideMainRepo(        newsApi: NewsApi    ) :MainRepo{        return MainRepo(api = newsApi)    }}