package uz.example.dagger_2_android3.core.modelimport android.os.Parcelimport android.os.Parcelableimport com.google.gson.annotations.SerializedNamedata class Article(    @SerializedName("source")    val source: Source?,    @SerializedName("author")    val author: String?, // Chris Isidore    @SerializedName("title")    val title: String?, // The US economy added 261,000 jobs in October, indicating that the Fed's efforts to cool the economy might be working - CNN    @SerializedName("description")    val description: String?, // The US economy added 261,000 jobs in October and the unemployment rate rose to 3.7%, according to the latest monthly employment snapshot from the Bureau of Labor Statistics released Friday morning.    @SerializedName("url")    val url: String?, // https://www.cnn.com/2022/11/04/economy/october-jobs-report-us/index.html    @SerializedName("urlToImage")    val urlToImage: String?, // https://media.cnn.com/api/v1/images/stellar/prod/221103154343-01-us-job-numbers-restricted.jpg?c=16x9&q=w_800,c_fill    @SerializedName("publishedAt")    val publishedAt: String?, // 2022-11-04T12:44:00Z    @SerializedName("content")    val content: String? // The US economy added 261,000jobs in October and the unemployment rate rose to 3.7%, according to the latest monthly employment snapshot from the Bureau of Labor Statistics released Friday morning.Tâ€¦ [+1231 chars]):Parcelable {    constructor(parcel: Parcel) : this(        parcel.readParcelable(Source::class.java.classLoader),        parcel.readString(),        parcel.readString(),        parcel.readString(),        parcel.readString(),        parcel.readString(),        parcel.readString(),        parcel.readString()    ) {    }    override fun writeToParcel(parcel: Parcel, flags: Int) {        parcel.writeParcelable(source, flags)        parcel.writeString(author)        parcel.writeString(title)        parcel.writeString(description)        parcel.writeString(url)        parcel.writeString(urlToImage)        parcel.writeString(publishedAt)        parcel.writeString(content)    }    override fun describeContents(): Int {        return 0    }    companion object CREATOR : Parcelable.Creator<Article> {        override fun createFromParcel(parcel: Parcel): Article {            return Article(parcel)        }        override fun newArray(size: Int): Array<Article?> {            return arrayOfNulls(size)        }    }}