package uz.example.dagger_2_android3.core.networkimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Queryimport uz.example.dagger_2_android3.core.model.TopHeadlineResponseimport uz.example.dagger_2_android3.core.model.latest.LatestResponseimport uz.example.dagger_2_android3.core.model.popular.PopularResponseinterface MovieDbApi {    @GET("/3/movie/latest")    suspend fun loadTopHeadline(@Query("api_key") apiKey: String, ): Response<LatestResponse?>    @GET("/3/movie/popular")    suspend fun loadPopular(@Query("api_key") apiKey: String ): Response<PopularResponse?>    @GET("/3/movie/upcoming")    suspend fun loadUpcoming(@Query("api_key") apiKey: String ): Response<PopularResponse?>}